/**
 * Get house name from token ID
 * @param {number|string} tokenId - The token ID to look up
 * @returns {string} - The house name or 'Unknown House'
 */
function getHouseFromTokenId(tokenId) {
    const house = TOKEN_HOUSE_MAPPING[tokenId.toString()];
    if (!house) {
        console.warn(`Token ID ${tokenId} not found in mapping`);
        return 'Unknown House';
    }
    return house;
}

/**
 * Get all token IDs for a specific house
 * @param {string} houseName - The house name to filter by
 * @returns {number[]} - Array of token IDs
 */
function getTokenIdsByHouse(houseName) {
    return Object.entries(TOKEN_HOUSE_MAPPING)
        .filter(([tokenId, house]) => house === houseName)
        .map(([tokenId]) => parseInt(tokenId))
        .sort((a, b) => a - b);
}

/**
 * Get summary of all houses with token counts
 * @returns {Object} - Object with house names as keys and counts as values
 */
function getHouseSummary() {
    const summary = {};
    Object.values(TOKEN_HOUSE_MAPPING).forEach(house => {
        summary[house] = (summary[house] || 0) + 1;
    });
    return summary;
}

